DIR = {
    '<': (-1, 0),
    '>': (1, 0)
}

def infinite_jets():
    while True:
        for x in input:
            #print(f"Jet: {x}")
            yield DIR[x]

jets = infinite_jets()

PIECES = [
    (((0,0), (1,0), (2,0), (3,0)),  '-'),
    (((0,1), (1,1), (2,1), (1,0), (1,2)), '+'),
    (((0,0), (1,0), (2,0), (2,1), (2,2)), 'L'),
    (((0,0), (0,1), (0,2), (0,3)), '|'),
     (((0,0), (1,0), (0,1), (1,1)), '#')
]

def generate_pieces():
    while True:
        for x in PIECES:
            yield x


height = 0

WIDTH = 7

# def row():
#     return [None] * 7
#
# grid = []

def shift(piece, offset):
    '''Shift the coordinates of a given piece by the given offset'''
    (x,y) = offset
    return ((xo+x, yo+y) for (xo, yo) in piece)

def empty_row():
    return [None] * WIDTH

class Grid:

    def __init__(self):
        self.__a = []


    def height(self):
        return len(self.__a)

    def __contains__(self, coords):
        (x,y) = coords
        return y < self.height() and bool(self.__a[y][x])

    def __setitem__(self, coords, val):
        (x,y) = coords
        while y >= self.height():
            self.__a.append(empty_row())
        self.__a[y][x] = val

    def just_drop(self, piece, xoffset=2):
        '''Drop a piece on the grid, without updating the grid
        Returns the Y offset where the piece lands'''
        for y in range(self.height()-1, -1, -1):
            for coords in shift(piece, (xoffset, y)):
                if coords in self:
                    return y+1
        return 0

    def is_filled(self, y):
        return all(self.__a[y])

    def clear_if_full(self, top, bot):
        for y in range(top, bot-1, -1):
            if self.is_filled(y):
                del self.__a[y]

    def old_insert(self, piece, xoffset, mark=True):
        '''Drop a piece on the grid, adding it to the grid and clearing full rows'''
        y = self.just_drop(piece, xoffset)
        p = list(shift(piece, (xoffset, y)))
        for c in p:
            self[c] = mark or True
        ys = [y for (x,y) in p]
        self.clear_if_full(max(ys), min(ys))

    def place(self, piece, mark=True):
        #print(f"Placing {piece}")
        for c in piece:
            self[c] = mark or True

    def insert(self, piece, mark=True):
        # Each rock appears so that its left edge is two units away from the left wall and its bottom edge is three units above the highest rock in the room (or the floor, if there isn't one).
        xoffset = 2
        y = self.height() + 3

        p = list(shift(piece, (xoffset, y)))
        #print(f"Starting y={y} for {p}")
        while True:
            dir = jets.__next__()
            shifted = list(shift(p, dir))
            if all(c[0] in range(0,WIDTH) and c not in self for c in shifted):
                p = shifted
            lower = list(shift(p, (0, -1)))
            #print(f"Checking {lower}")
            if any(c[1]<0 or c in self for c in lower):
                self.place(p, mark)
                return p
            p = lower
    def print(self, limit):
        h = self.height()
        stop = max(h-limit, -1) if limit else -1
        for y in range(h-1, stop, -1):
            print('|', end='')
            for c in self.__a[y]:
                if type(c) == str:
                    p = c
                elif c:
                    p = '@'
                else:
                    p = '.'
                print(p, end='')
            print('|')
        print('+-------+')

grid = Grid()

print('--------------------------------------------------')

def simulate_n(n, pieces):
    for i in range(n):
        (p, m) = pieces.__next__()

        #if i < 11:
        #    print(f"After {i} pieces:")
        #    grid.print()
        #else: break
        #print(f"Piece {i}: {p}")
        newp = grid.insert(p, m)
        if i == 0:
            print(f"Started with {m}: {p}")
            grid.print(5)
        if i == n-1:
            print(f"Ended with {m}: {p}")
            grid.print(5)
            return newp

def part1():
    simulate_n(2022, generate_pieces())
    print(grid.height())
    assert grid.height() == 3059

def part2():
    N = 1000000000000
    pieces = generate_pieces()

    lcm = len(PIECES) * len(input) # too big, but good enough?

    simulate_n(lcm, pieces)
    base_height = grid.height()
    print(f"Base height after {lcm} pieces: {base_height}")


    def try_lcm():
        old_height = grid.height()
        p = simulate_n(lcm, pieces)
        addl_height = grid.height() - old_height
        print(f"Found additional height {addl_height} after {lcm} more pieces")
        return p


    try1p = try_lcm()
    try2p = try_lcm()
    n = 2
    while try2p[0][0] != try1p[0][0]: # leftmost is different
        try2p = try_lcm()
        n += 1

    cycle_time = n * lcm
    cycle_height = grid.height() - base_height

    print(f"Found cycle! cycle_time={cycle_time} cycle_height={cycle_height} n={n}")

    repetitions = (N - lcm ) // cycle_time
    remaining = (N - lcm) % cycle_time
    old_height = grid.height()
    simulate_n(remaining, pieces)
    rem_height = grid.height() - old_height

    print(f"Repetitions = {repetitions}, remaining={remaining}, rem_height={rem_height}")

    print(base_height + (cycle_height*repetitions) + rem_height)

